loop.script:
var t1 = new map();
t1.NAME = '基础保障';
t1.HEADER = [['保障项目', '保险金额', '保费']];
t1.CONTENT = new list();

var t2 = new map();
t2.NAME = '医疗费用补偿';
t2.HEADER = [['保障项目', '保险金额', '附加保障信息', '保费']];
t2.CONTENT = new list();

var t3 = new map();
t3.NAME = '公共交通意外伤害保障';
t3.HEADER = [['保障项目', '保险金额', '保费']];
t3.CONTENT = new list();

var t4 = new map();
t4.NAME = '住院津贴保障';
t4.HEADER = [['保障项目', '保险金额', '附加保障信息', '保费']];
t4.CONTENT = new list();

for (var p : val(PLAN.DETAIL)) {
if (p.DETAIL == null) {
if (p.AMOUNT != null and p.AMOUNT > 0){
if (p.CODE == 'C006'){
if(p.CLAUSE_ITEM == '2')
p.NAME='意外住院津贴（仅含意外）';
else
p.NAME='综合住院津贴（含意外、疾病）';

t4.CONTENT += [p.NAME, p.AMOUNT+'元/天', p.EXTRA==null?'':p.EXTRA, p.PREMIUM];

}else{
t1.CONTENT += [p.NAME, p.AMOUNT, p.PREMIUM];
}
}
} else {
for (var q : val(p.DETAIL)) {

if (q.AMOUNT != null and q.CODE != null and q.AMOUNT > 0) {
if (str_begin(q.CODE, 'C002')) {
if (q.CODE == 'C00204' and q.EXP == 1) {
t3.CONTENT += [q.NAME+'(扩展自驾、乘坐私家车)', q.AMOUNT, q.PREMIUM];
} else {
t3.CONTENT += [q.NAME, q.AMOUNT, q.PREMIUM];
}

}

else if (str_begin(q.CODE, 'C005'))
t2.CONTENT += [q.NAME, q.AMOUNT, (q.EXTRA==null?'':q.EXTRA)+(q.TYPE_B==1?' 含乙类药品保障':''), q.PREMIUM];
}
}
}
}
var tables = new list();
if (size(t1.CONTENT) > 0)
tables += t1;
if (size(t3.CONTENT) > 0)
tables += t3;
if (size(t2.CONTENT) > 0)
tables += t2;
if (size(t4.CONTENT) > 0)
tables += t4;

paragraph.script:
var agreement = new object[size(PLANS)][2];
for (var i=0;size(PLANS)>i;i++) {
agreement[i][0] = PLANS[i].NAME;
agreement[i][1] = PLANS[i].SPAG;
}

var ac = new map();
ac.L1 = '团体人身意外伤害保险条款';
ac.L2 = '团体疾病身故与全残保险条款';
ac.L3 = '营运交通工具团体人身意外伤害保险条款';
ac.L4 = '团体重大疾病保险条款';
ac.L5 = '附加团体意外伤害医疗保险条款';
ac.L6 = '团体住院津贴医疗保险条款';
ac.L7 = '团体门诊急诊医疗保险条款';
ac.L8 = '团体住院医疗保险条款';
ac.L9 = '团体生育医疗保险条款';
ac.L10 = '附加团体公共保额医疗保险条款';

var clauses = new list();
for (var plan : PLANS) {
for (var p : val(plan.DETAIL)) {
//print('*****'+p);
if (p.AMOUNT != null and p.AMOUNT > 0) {
if (p.CODE == 'C001') {
if (find(clauses, 'L1') == null) clauses += 'L1';
} else if (p.CODE == 'C002') {
if (find(clauses, 'L3') == null) clauses += 'L3';
} else if (p.CODE == 'C003') {
if (find(clauses, 'L2') == null) clauses += 'L2';
} else if (p.CODE == 'C004') {
if (find(clauses, 'L4') == null) clauses += 'L4';
} else if (p.CODE == 'C006') {
if (find(clauses, 'L6') == null) clauses += 'L6';
}
}
if (p.DETAIL != null) {
for (var q : val(p.DETAIL)) {
//print('*********'+q);
if (q.AMOUNT != null and q.AMOUNT > 0 and q.CODE != null) {
if (q.CODE == 'C00501' or q.CODE == 'C00501A') {
if (find(clauses, 'L7') == null) clauses += 'L7';
}
if (q.CODE == 'C00501' or q.CODE == 'C00501B') {
if (find(clauses, 'L8') == null) clauses += 'L8';
}
if (str_begin(q.CODE, 'C002')) {
if (find(clauses, 'L3') == null) clauses += 'L3';
} else if (q.CODE == 'C00502') {
if (find(clauses, 'L5') == null) clauses += 'L5';
} else if (q.CODE == 'C00503') {
if (find(clauses, 'L9') == null) clauses += 'L9';
} else if (q.CODE == 'C00504') {
if (find(clauses, 'L10') == null) clauses += 'L10';
}
}
}
}
}
}
